{

    "name": "Cert_SSO",

    "apiAnonymous": "when SERVERSSL_CLIENTHELLO_SEND {

set username [ACCESS::session data get "session.logon.last.username"]
set domain [ACCESS::session data get "session.ad.last.actualdomain"]
  SSL::c3d extension 1.1.1.1 "Minted Extension=$username@$domain"
}

when ACCESS_PER_REQUEST_AGENT_EVENT {
 set cert [ACCESS::session data get {session.ssl.cert.whole}]
 log local0. "My cert: $cert"
 SSL::c3d cert [X509::pem2der $cert]
}"
}

{
    "name": "{{VS_NAME}}-psp_act_variable_assign_ag",
    "partition": "Common",
    "type": "general",
    "variables": [
        {
            "append": "false",
            "expression": "set x509e_fields [split [mcget {session.ssl.cert.x509extension}] \\\"\\\\n\\\"];\n# For each element in the list:\nforeach field $x509e_fields {\n# If the element contains UPN:\nif { $field contains \\\"othername:UPN\\\" } {\n## set start of UPN variable\nset start [expr {[string first \\\"othername:UPN<\\\" $field] +14}]\n# UPN format is <user@domain>\n# Return the UPN, by finding the index of opening and closing brackets, then use string range to get everything between.\nreturn [string range $field $start [expr { [string first \\\">\\\" $field $start] - 1 } ] ];  } }\n# Otherwise return UPN Not Found:\nreturn \\\"UPN-NOT-FOUND\\\";\n",
            "secure": "false",
            "varname": "session.custom.upn"
        }
    ]
}

{

    "name": "{{VS_NAME}}-psp_act_variable_assign_1_ag",
    "partition": "Common",
    "type": "general",
    "variables": [
        {
            "append": "false",
            "expression": "if { [mcget {session.ldap.last.attr.memberOf}] contains \\\"CN=sp.acme.com-globaladmin,OU=Groups,DC=f5lab,DC=local\\\" } { return \\\"globaladmin-100\\\"; }",
            "secure": "false",
            "varname": "session.custom.100.globaladmin"
        },
        {
            "append": "false",
            "expression": "if { [mcget {session.ldap.last.attr.memberOf}] contains \\\"CN=sp.acme.com-admin,OU=Groups,DC=f5lab,DC=local\\\" } { return \\\"admin-100\\\"; }",
            "secure": "false",
            "varname": "session.custom.100.admin"
        },
        {
            "append": "false",
            "expression": "if { [mcget {session.ldap.last.attr.memberOf}] contains \\\"CN=sp.acme.com-readonly,OU=Groups,DC=f5lab,DC=local\\\" } { return \\\"readonly-100\\\"; }",
            "secure": "false",
            "varname": "session.custom.100.readonly"
        }
    ]
}

{
    "name": "{{VS_NAME}}-psp_act_ad_group_mapping_ag",
    "partition": "Common",
    "type": "ad-group-mapping",
    "rules": [
        {
            "expression": "expr { [string tolower [mcget -decode {session.ad.last.attr.memberOf}]] contains [string tolower \\\"CN=Sales Engineering,CN=Users,DC=f5lab,DC=local\\\"] }",
            "networkAccessResources": [
                "/Common/{{VS_NAME}}-vpn"
            ],
            "portalAccessResources": [
                "/Common/{{VS_NAME}}-server1",
  
            ],
            "webtop": "/Common/{{VS_NAME}}-webtop",
            "webtopSections": [
                "/Common/{{VS_NAME}}-network_access"
            ]
        },
        {
            "expression": "expr { [string tolower [mcget -decode {session.ad.last.attr.memberOf}]] contains [string tolower \\\"CN=Product Management,CN=Users,DC=f5lab,DC=local\\\"] }",
            "portalAccessResources": [
                "/Common/{{VS_NAME}}-server1",
            ],
            "webtop": "/Common/{{VS_NAME}}-webtop"
        },
        {
            "expression": "expr { [string tolower [mcget -decode {session.ad.last.attr.memberOf}]] contains [string tolower \\\"CN=Product Development,CN=Users,DC=f5lab,DC=local\\\"] }",
            "webtop": "/Common/{{VS_NAME}}-webtop"
        },
        {
            "expression": "expr { [string tolower [mcget -decode {session.ad.last.attr.memberOf}]] contains [string tolower \\\"CN=IT,CN=Users,DC=f5lab,DC=local\\\"] }",
            "networkAccessResources": [
                "/Common/{{VS_NAME}}-vpn"
            ],
            "webtop": "/Common/{{VS_NAME}}-webtop"
        }
    ]
}

